import { __decorate } from "tslib";
import { Component } from '@angular/core';
let CharacterDetailComponent = class CharacterDetailComponent {
    constructor(dataService, route) {
        this.dataService = dataService;
        this.route = route;
    }
    async ngOnInit() {
        this.route.params.subscribe((params) => {
            this.characterId = params['id'];
            console.log(params['id']);
        });
        this.dataService.getCharacters(this.characterId).subscribe(response => {
            this.characterInfo = response;
        });
    }
};
CharacterDetailComponent = __decorate([
    Component({
        selector: 'app-character-detail',
        templateUrl: './character-detail.component.html',
        styleUrls: ['./character-detail.component.css']
    })
], CharacterDetailComponent);
export { CharacterDetailComponent };
